
#include <stdlib.h>
#include <stdio.h>

int charToInt(char c);
long long productLargestSequence(char*, int numberOfGroup);

int main(){

  char* string = "  ";
  long long prod = productLargestSequence(string, 13);


  printf("Answer = %lld\n\n",prod);
  return EXIT_SUCCESS;
}

long long productLargestSequence(char* str, int numberOfGroup){
  int ref = 0;
  int stopped = 0;
  long long biggestProd = 0;
  long long currentProd = 1;

  while(str[stopped + ref] != '\0'){
    if(ref < numberOfGroup){
      currentProd*=charToInt(str[stopped + ref]);
      ref++;
    }else{
      ref = 0;
      if(biggestProd < currentProd){
        biggestProd = currentProd;
      }
      currentProd = 1;
      stopped++;
    }
  }
  return biggestProd;
}

int charToInt(char c){
  switch (c) {
    case '0':
      return 0;
    case '1':
      return 1;
    case '2':
      return 2;
    case '3':
      return 3;
    case '4':
      return 4;
    case '5':
      return 5;
    case '6':
      return 6;
    case '7':
      return 7;
    case '8':
      return 8;
    case '9':
      return 9;
  }
}
